import React, { useState } from 'react';
import './Footer.css';
import { RiFacebookFill, RiInstagramFill, RiYoutubeFill, RiLinkedinFill, RiMailFill, RiPhoneFill, RiMapPinFill } from "react-icons/ri";
import { useNavigate } from 'react-router-dom';

const Footer = () => {
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleNavigation = (path) => {
    setLoading(true); // Activa el estado de carga
    setTimeout(() => {
      navigate(path); // Redirige después de un pequeño retraso
      setLoading(false); // Desactiva el estado de carga
    }, 1000); // Ajusta el tiempo de carga (en milisegundos)
  };

  return (
    <footer className="footer">
      {/* Efecto de carga */}
      {loading && <div className="loading-overlay">Cargando...</div>}

      {/* Redes sociales */}
      <div className="footer-socials">
        <a href="https://www.facebook.com" target="_blank" rel="noopener noreferrer" className="social-icon">
          <RiFacebookFill size={30} />
        </a>
        <a href="https://www.instagram.com" target="_blank" rel="noopener noreferrer" className="social-icon">
          <RiInstagramFill size={30} />
        </a>
        <a onClick={() => handleNavigation('/events')} className="social-icon" style={{ cursor: 'pointer' }}>
          <RiYoutubeFill size={30} />
        </a>
        <a href="https://www.linkedin.com" target="_blank" rel="noopener noreferrer" className="social-icon">
          <RiLinkedinFill size={30} />
        </a>
      </div>

      {/* Información de contacto */}
      <div className="footer-info">
        <div className="info-item">
          <RiMapPinFill size={20} className="info-icon" />
          <p><strong>Dirección:</strong> Imbabura, Atuntaqui, Ecuador.</p>
        </div>
        <div className="info-item">
          <RiPhoneFill size={20} className="info-icon" />
          <p><strong>Celular:</strong> +593 00 000 0000</p>
        </div>
        <div className="info-item">
          <RiMailFill size={20} className="info-icon" />
          <p><strong>Correo:</strong> user@gmail/outlook.com</p>
        </div>
      </div>

      {/* Copyright */}
      <div className="footer-copyright">
        <p>Copyright © 2025 Marck. Todos los derechos reservados</p>
      </div>
    </footer>
  );
};

export default Footer;
