import React from 'react';
import './HomePage.css';

const HomePage = () => {
  return (
    <div className="homepage">
      {/* Sección de imagen del evento */}
      <div className="event-section">
        <img
          src={require('../assets/images/donbolo.jpeg')}// Cambia esta URL por la imagen del evento
          alt="Evento principal"
          className="event-image"
        />
        <div className="event-overlay">
          <h1>Evento Principal</h1>
          <p>¡No te pierdas este gran evento!</p>
        </div>
      </div>

      {/* Slider de partidos */}
      <div className="matches-slider">
        <h2>Partidos Destacados</h2>
        <div className="slider">
          <div className="slide">Partido 1</div>
          <div className="slide">Partido 2</div>
          <div className="slide">Partido 3</div>
          <div className="slide">Partido 4</div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
