import React, { useState } from 'react';
import './HomePage.css';
import image1 from '../assets/images/image1.jpeg';
import image2 from '../assets/images/image2.jpg';
import image3 from '../assets/images/image3.jpg';

const HomePage = () => {
  const images = [image1, image2, image3]; // Lista de imágenes
  const [currentIndex, setCurrentIndex] = useState(0);

  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="homepage">
      {/* Slider de imágenes */}
      <div className="slider-container">
        <button className="slider-btn left" onClick={prevSlide}>
          &#8592;
        </button>
        <img
          src={images[currentIndex]}
          alt={`Slide ${currentIndex + 1}`}
          className="slider-image"
        />
        <button className="slider-btn right" onClick={nextSlide}>
          &#8594;
        </button>
      </div>

      {/* Slider de partidos */}
      <div className="matches-slider">
        <h2>Partidos Destacados</h2>
        <div className="slider">
          <div className="slide">Partido 1</div>
          <div className="slide">Partido 2</div>
          <div className="slide">Partido 3</div>
          <div className="slide">Partido 4</div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
