import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css'; // Assuming you will create a Navbar.css for styling

const Navbar = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const images = [
    { src: 'image1.jpg', link: 'https://example.com/1' },
    { src: 'image2.jpg', link: 'https://example.com/2' },
    { src: 'image3.jpg', link: 'https://example.com/3' },
  ];

  const prevSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  const nextSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === images.length - 1 ? 0 : prevIndex + 1
    );
  };

  return (
    <div className="homepage">
      {/* Header */}
      <header className="header">
        <div className="header-left">
          <h1>Nombre de la Página</h1>
        </div>
        <div className="header-right">
          <a href="#home" className="header-link">Home</a>
        </div>
      </header>

      {/* Slider de imágenes */}
      <div className="slider-container">
        <button className="slider-btn left" onClick={prevSlide}>
          &#8592;
        </button>
        <div
          className="slider-track"
          style={{
            transform: `translateX(-${currentIndex * 100}%)`,
            width: `${images.length * 100}%`,
          }}
        >
          {images.map((image, index) => (
            <a
              key={index}
              href={image.link}
              target="_blank"
              rel="noopener noreferrer"
              className="slider-link"
            >
              <img
                src={image.src}
                alt={`Slide ${index + 1}`}
                className="slider-image"
              />
            </a>
          ))}
        </div>
        <button className="slider-btn right" onClick={nextSlide}>
          &#8594;
        </button>
      </div>
    </div>
  );
};

export default Navbar;
