import React, { useState, useEffect, useRef } from 'react';
import './HomePage.css';
import { Link } from 'react-router-dom';
import image1 from '../assets/images/image1.jpg';
import image2 from '../assets/images/image2.jpg';
import image3 from '../assets/images/historia.jpg';

import Footer from '../components/Footer';

const HomePage = () => {
  const images = [
    { src: image1, link: 'https://www.youtube.com/watch?v=z2Y8WJHDTo0' },
  ];

  const [currentIndex, setCurrentIndex] = useState(0);
  const [isDragging, setIsDragging] = useState(false);
  const [startX, setStartX] = useState(0);
  const sliderRef = useRef(null);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 7000);

    return () => clearInterval(interval);
  }, [images.length]);

  const handleMouseDown = (e) => {
    setIsDragging(true);
    setStartX(e.clientX);
  };

  const handleMouseMove = (e) => {
    if (!isDragging) return;
    const diff = e.clientX - startX;
    if (diff > 50) {
      prevSlide();
      setIsDragging(false);
    } else if (diff < -50) {
      nextSlide();
      setIsDragging(false);
    }
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const handleTouchStart = (e) => {
    setIsDragging(true);
    setStartX(e.touches[0].clientX);
  };

  const handleTouchMove = (e) => {
    if (!isDragging) return;
    const diff = e.touches[0].clientX - startX;
    if (diff > 50) {
      prevSlide();
      setIsDragging(false);
    } else if (diff < -50) {
      nextSlide();
      setIsDragging(false);
    }
  };

  const handleTouchEnd = () => {
    setIsDragging(false);
  };

  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  return (
    <div
      className="homepage"
      onMouseDown={handleMouseDown}
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
      onTouchStart={handleTouchStart}
      onTouchMove={handleTouchMove}
      onTouchEnd={handleTouchEnd}
      ref={sliderRef}
    >
      <div className="slider-container">
        <button className="slider-btn left" onClick={prevSlide}>
          &#8592;
        </button>
        <div className="slider-track" style={{
          transform: `translateX(-${currentIndex * 50}%)`,
          width: `${images.length * 100}%`,
        }}>
          {images.map((image, index) => (
            index === 0 ? (
              <Link
                key={index}
                to="/events" // Ruta a la página de eventos
                className="slider-link"
              >
                <img
                  src={image2.src}
                  alt={`Slide ${index + 1}`}
                  className="slider-image"
                />
              </Link>
            ) : (
              <a
                key={index}
                href={image.link}
                target="_blank"
                rel="noopener noreferrer"
                className="slider-link"
              >
                <img
                  src={image.src}
                  alt={`Slide ${index + 1}`}
                  className="slider-image"
                />
              </a>
            )
          ))}
        </div>
        <button className="slider-btn right" onClick={nextSlide}>
          &#8594;
        </button>
      </div>

      {/* Sección de la historia */}
      <section className="history-section">
        <h2 className="history-title">Nuestra Historia</h2>
        <p className="history-text">
          Nuestra cultura es un reflejo de nuestras raíces, tradiciones y valores. A través de los años, hemos preservado nuestras costumbres, música, danzas y gastronomía, que son el alma de nuestra identidad. Cada rincón de nuestra tierra cuenta una historia, y cada tradición es un legado que compartimos con orgullo.
        </p>
        <img
          src={image3}
          alt="Imagen debajo del slider"
          className="below-slider-image"
        />
      </section>

      <Footer />
    </div>
  );
};

export default HomePage;
