import React from 'react';
import './EventPage.css';
import image1 from '../assets/images/image1.jpg'; // Importa la primera imagen
import image2 from '../assets/images/image2.jpg'; // Importa la segunda imagen
import bannerImage from '../assets/images/image1.jpg'; // Importa la imagen del banner

const EventPage = () => {
  const events = [
    {
      id: 1,
      title: 'Evento 1',
      description: 'Descripci贸n del evento 1.',
      date: 'Fecha del evento',
      image: image1, // Usa la imagen importada
    },
    {
      id: 2,
      title: 'Evento 2',
      description: 'Descripci贸n del evento 2.',
      date: 'Fecha del evento',
      image: image2, // Usa la imagen importada
    },
  ];

  return (
    <div className="event-page">
      <div className="banner-container">
        <img src={bannerImage} alt="Banner" className="banner-image" />
        <div className="banner-text">
          <h1>Bienvenidos a Nuestros Eventos</h1>
          <p>Descubre lo mejor de nuestra comunidad</p>
        </div>
      </div>
      {/* Resto del contenido */}
    </div>
  );
};

export default EventPage;
