import React, { useState, useEffect, useRef } from 'react';
import './HomePage.css';
import { Link, useNavigate, useHistory } from 'react-router-dom';
import image1 from '../assets/images/imagen4.jpg';
import image2 from '../assets/images/image2.jpg';
import image3 from '../assets/images/image6.jpeg';
import image4 from '../assets/images/image5.png';
import logo from '../assets/images/icon.jpg'; // Importa el logo
import Footer from '../components/Footer';

const HomePage = () => {
  const [loading, setLoading] = useState(false); // Estado de carga
  const history = useHistory(); // Cambia useNavigate por useHistory
  const images = [
    { src: image1, link: '/events' },
    { src: image4, link: 'https://admin.torneopro.app/967e77a2-f7f3-4e0e-8eb1-6daa490dee31/competencia/55/fases-grupos' },
  ];

  const [currentIndex, setCurrentIndex] = useState(0);
  const [isDragging, setIsDragging] = useState(false);
  const [startX, setStartX] = useState(0);
  const sliderRef = useRef(null);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 7000);

    return () => clearInterval(interval);
  }, [images.length]);

  const handleNavigation = (link) => {
    setLoading(true); // Activa el estado de carga
    setTimeout(() => {
      if (link.startsWith('/')) {
        history.push(link); // Redirige a rutas internas
      } else {
        window.location.href = link; // Redirige a enlaces externos
      }
      setLoading(false); // Desactiva el estado de carga
    }, 1000); // Ajusta el tiempo de carga seg√∫n sea necesario
  };

  const handleMouseDown = (e) => {
    setIsDragging(true);
    setStartX(e.clientX);
  };

  const handleMouseMove = (e) => {
    if (!isDragging) return;
    const diff = e.clientX - startX;
    if (diff > 50) {
      prevSlide();
      setIsDragging(false);
    } else if (diff < -50) {
      nextSlide();
      setIsDragging(false);
    }
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const handleTouchStart = (e) => {
    setIsDragging(true);
    setStartX(e.touches[0].clientX);
  };

  const handleTouchMove = (e) => {
    if (!isDragging) return;
    const diff = e.touches[0].clientX - startX;
    if (diff > 50) {
      prevSlide();
      setIsDragging(false);
    } else if (diff < -50) {
      nextSlide();
      setIsDragging(false);
    }
  };

  const handleTouchEnd = () => {
    setIsDragging(false);
  };

  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  return (
    <div
      className="homepage"
      onMouseDown={handleMouseDown}
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
      onTouchStart={handleTouchStart}
      onTouchMove={handleTouchMove}
      onTouchEnd={handleTouchEnd}
      ref={sliderRef}
    >
      {/* Efecto de carga */}
      {loading && (
        <div className="loading-overlay">
          <img src={logo} alt="Cargando..." className="loading-logo" />
        </div>
      )}

      <div className="slider-container">
        <button className="slider-btn left" onClick={prevSlide}>
          &#8592;
        </button>
        <div className="slider-track" style={{
          transform: `translateX(-${currentIndex * 50}%)`,
          width: `${images.length * 100}%`,
        }}>
          {images.map((image, index) => (
            <div
              key={index}
              className="slider-link"
              onClick={() => handleNavigation(image.link)} // Maneja la redirecci√≥n con animaci√≥n
            >
              <img
                src={image.src}
                alt={`Slide ${index + 1}`}
                className="slider-image"
              />
            </div>
          ))}
        </div>
        <button className="slider-btn right" onClick={nextSlide}>
          &#8594;
        </button>
      </div>

      {/* Secci√≥n de la historia */}
      <section className="history-section">
        <h2 className="history-title">Nuestra Historia</h2>
        <p className="history-text">
          Nuestra cultura es un reflejo de nuestras ra√≠ces, tradiciones y valores. A trav√©s de los a√±os, hemos preservado nuestras costumbres, m√∫sica, danzas y gastronom√≠a, que son el alma de nuestra identidad. Cada rinc√≥n de nuestra tierra cuenta una historia, y cada tradici√≥n es un legado que compartimos con orgullo.
        </p>
        <img
          src={image3}
          alt="Imagen debajo del slider"
          className="below-slider-image"
        />
      </section>

      {/* Secci√≥n de la founders */}
      <section className="founders-section">
          <h2 className="founders-title">Nuestros miembros Fundadores</h2>

          <img
              src={image2}
              alt="Imagen debajo del slider"
              className="below-slider-image"
          />
          <ul className="founders-list">
              <li className="founder-item">Marco Humberto Lima Maigua</li>
              <li className="founder-item">Luis Laurentino Chiza Lema</li>
              <li className="founder-item">Carlos Segundo Manrique Potosi</li>
              <li className="founder-item">Mar√≠a Rebeca Lima Maigua</li>
              <li className="founder-item">Robertina Lema Chiza</li>
              <li className="founder-item">Julio Enrique Liman Moran</li>
              <li className="founder-item">Segundo Jos√© Lema Chiza</li>
              <li className="founder-item">Rafael Lema Chiza</li>
              <li className="founder-item">Alex Javier Lima Amagua√±a</li>
              <li className="founder-item">Jos√© Joaqu√≠n Lima Chiza</li>
              <li className="founder-item">Juan Xavier Duque Saransing</li>
              <li className="founder-item">Diego Armando Lema Cordova</li>
          </ul>
      </section>

      <section className="mission-vision-section">
        <div className="mv-container">
          {/* Tarjeta Misi√≥n */}
          <div className="mv-card mission-card">
            <div className="mv-icon">üó∫Ô∏è</div> {/* Puedes usar un icono o imagen */}
            <h3>MISI√ìN</h3>
            <p className="mv-text">
              Texto descriptivo que explica el prop√≥sito central de tu organizaci√≥n.
              Ejemplo: "Impulsar soluciones innovadoras para conectar a las personas..."
            </p>
          </div>

          {/* Tarjeta Visi√≥n */}
          <div className="mv-card vision-card">
            <div className="mv-icon">üî≠</div>
            <h3>VISI√ìN</h3>
            <p className="mv-text">
              Texto inspirador que describe el futuro al que aspiran. Ejemplo: "Ser l√≠deres globales en transformaci√≥n digital para 2030..."
            </p>
          </div>
        </div>
      </section>


      <Footer />
    </div>
  );
};

export default HomePage;
