import React, { useState, useEffect } from 'react';
import './HomePage.css';
import image1 from '../assets/images/image1.jpeg';
import image2 from '../assets/images/image2.jpg';

const HomePage = () => {
  const images = [
    { src: image1, link: 'https://www.facebook.com/profile.php?id=100016839129777&locale=es_LA' },
    { src: image2, link: 'https://www.youtube.com/watch?v=z2Y8WJHDTo0' }, // Nueva imagen con enlace
  ]; // Lista de im치genes con enlaces

  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 7000); // Cambia cada 7 segundos

    return () => clearInterval(interval); // Limpia el intervalo al desmontar
  }, [images.length]);

  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="homepage">
      {/* Header */}
      <header className="header">
        <div className="header-left">
          <h1>Nombre de la P치gina</h1>
        </div>
        <div className="header-right">
          <a href="#home" className="header-link">Home</a>
        </div>
      </header>

      {/* Slider de im치genes */}
      <div className="slider-container">
        <button className="slider-btn left" onClick={prevSlide}>
          &#8592;
        </button>
        <div
          className="slider-track"
          style={{
            transform: `translateX(-${currentIndex * 100}%)`,
            width: `${images.length * 100}%`,
          }}
        >
          {images.map((image, index) => (
            <a
              key={index}
              href={image.link}
              target="_blank"
              rel="noopener noreferrer"
              className="slider-link"
            >
              <img
                src={image.src}
                alt={`Slide ${index + 1}`}
                className="slider-image"
              />
            </a>
          ))}
        </div>
        <button className="slider-btn right" onClick={nextSlide}>
          &#8594;
        </button>
      </div>
    </div>
  );
};

export default HomePage;
